const axios = require("axios");
const url =
  "https://www.receitaws.com.br/v1/cnpj/03051279000120";
axios
  .get(url)
  .then(response => {
    console.log(
      `City: ${response.data.atividade_principal[0].text} -`,
      `Latitude: ${response.data.atividade_principal[0].text} -`,
      `Longitude: ${response.data.atividade_principal[0].text}`
    );
  })
  .catch(error => {
    console.log(error);
  });